svg.script:

document.addEventListener('DOMContentLoaded', function() {
  try {
    const container = document.getElementById('svg-form-container');
    if (!container) return;

    // Stwórz kontener HTML w SVG
    const fo = document.createElementNS('http://www.w3.org/2000/svg', 'foreignObject');
    fo.setAttribute('x', '20');
    fo.setAttribute('y', '80');
    fo.setAttribute('width', '360');
    fo.setAttribute('height', '470');

    const div = document.createElementNS('http://www.w3.org/1999/xhtml', 'div');

    // Wstaw formularz HTML
    div.innerHTML = `
      <form id="interactive-form" style="width:100%;height:100%;padding:10px;box-sizing:border-box;background:transparent;">
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Username</label><input type="text" name="username" placeholder="Enter your username" style="width:100%;padding:8px 10px;background:#1f1f1f;border:1px solid #404040;border-radius:4px;color:#e5e7eb;font-size:14px;box-sizing:border-box;" /></div>
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Email Address</label><input type="email" name="email" placeholder="your.email@example.com" style="width:100%;padding:8px 10px;background:#1f1f1f;border:1px solid #404040;border-radius:4px;color:#e5e7eb;font-size:14px;box-sizing:border-box;" /></div>
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Password</label><input type="password" name="password" style="width:100%;padding:8px 10px;background:#1f1f1f;border:1px solid #404040;border-radius:4px;color:#e5e7eb;font-size:14px;box-sizing:border-box;" /></div>
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Country</label><select name="country" style="width:100%;padding:8px 10px;background:#1f1f1f;border:1px solid #404040;border-radius:4px;color:#e5e7eb;font-size:14px;box-sizing:border-box;"><option value="">Select your country</option><option value="pl">Poland</option><option value="de">Germany</option><option value="fr">France</option><option value="es">Spain</option><option value="it">Italy</option><option value="uk">United Kingdom</option><option value="us">United States</option></select></div>
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Bio</label><textarea name="bio" placeholder="Tell us about yourself..." rows="3" style="width:100%;padding:8px 10px;background:#1f1f1f;border:1px solid #404040;border-radius:4px;color:#e5e7eb;font-size:14px;box-sizing:border-box;resize:vertical;"></textarea></div>
        <div style="margin-bottom:15px;display:flex;align-items:center;"><input type="checkbox" name="terms" style="margin-right:8px;" /><label style="color:#9ca3af;font-size:12px;">I agree to the Terms and Conditions</label></div>
        <div style="margin-bottom:15px;"><label style="display:block;color:#9ca3af;font-size:12px;margin-bottom:5px;">Newsletter Subscription</label><div style="display:inline-block;margin-right:20px;"><input type="radio" name="newsletter" value="yes" style="margin-right:5px;" /><label style="color:#9ca3af;font-size:12px;">Yes, subscribe me</label></div><div style="display:inline-block;margin-right:20px;"><input type="radio" name="newsletter" value="no" style="margin-right:5px;" /><label style="color:#9ca3af;font-size:12px;">No, thanks</label></div></div>
        <button type="submit" style="width:100%;margin-top:20px;padding:12px;background:#3b82f6;color:white;border:none;border-radius:6px;font-size:14px;font-weight:600;cursor:pointer;">Create Account</button>
      </form>
    `;

    // Dodaj obsługę formularza
    setTimeout(() => {
      const form = div.querySelector('#interactive-form');
      if (form) form.addEventListener('submit', function(e) {
        e.preventDefault();
        alert('Form submitted successfully!');
      });
    }, 0);

    // Dodaj obsługę formularza
    setTimeout(() => {
      const form = div.querySelector('#interactive-form');
      if (form) form.addEventListener('submit', function(e) {
        e.preventDefault();
        alert('Form submitted successfully!');
      });
    }, 0);

    container.innerHTML = '';
    fo.appendChild(div);
    container.appendChild(fo);
  } catch (error) {
    console.error('Form error:', error);
  }
});
